import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";

const DetailUser = () => {
  const [user, setUser] = useState({});
  const [pelanggarans, setPelanggarans] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    // Ambil data pengguna berdasarkan ID
    axios.get(`http://localhost:5000/users/${id}`)
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
      });

    // Ambil semua data pelanggaran
    axios.get("http://localhost:5000/pelanggarans")
      .then((response) => {
        // Filter pelanggaran berdasarkan ID pengguna
        const filteredPelanggarans = response.data.filter((pelanggaran) => pelanggaran.user === id);
        setPelanggarans(filteredPelanggarans);
      })
      .catch((error) => {
        console.error("Error fetching pelanggaran data:", error);
      });
  }, [id]);
  const handlePointDeduction = (pelanggaran) => {
    const updatedPoint = user.point - pelanggaran.Kurangpoint;
    setUser({ ...user, point: updatedPoint });
  };
  return (
    <div className="box" style={{ width: "75%", margin: "0 auto", marginTop: "50px", paddingBottom: "70px" }}>
      <div><h1 style={{ textAlign: "center", fontSize: "2rem", marginBottom: "30px" }}>Detail Informasi Pelanggaran</h1></div>
      <div className="columns">
        <div className="column is-1">Nama : </div>
        <div className="column">{user.name}</div>
      </div>
      <div className="columns">
        <div className="column is-1">Email : </div>
        <div className="column">{user.email}</div>
      </div>
      <div className="columns">
        <div className="column is-1">Point : </div>
        <div className="column">{user.point}</div>
      </div>
      <div className="container mt-5">
        <div className="columns is-centered">
          <div className="column is-four-fifths">
            <Link to={"add/pelanggaran"} className="button is-primary is-rounded">
              Tambah
            </Link>
            <div className="table-container">
              <table className="table is-fullwidth is-striped is-hoverable is-responsive">
                <thead>
                  <tr>
                    <th>No</th>
                    <th>Tanggal</th>
                    <th>Pelanggaran</th>
                    <th>Pengurangan Point</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {pelanggarans.map((pelanggaran, index) => (
                    <tr key={pelanggaran._id}>
                      <td>{index + 1}</td>
                      <td>{pelanggaran.date}</td>
                      <td>{pelanggaran.keterangan}</td>
                      <td>{pelanggaran.Kurangpoint}</td>
                      <td>
                        <Link to={`edit/${pelanggaran._id}`} className="button is-warning is-rounded">
                          Edit
                        </Link>
                        <button className="button is-danger is-rounded">
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetailUser;
