import React,{useState,useEffect} from 'react';
import axios from 'axios';
import { Link } from "react-router-dom";


const UserList = () => {
const [users, setUser] = useState([]);

    useEffect(() => {
      getUsers();
    }, []);
  
    const getUsers = async () => {
      const response = await axios.get("http://localhost:5000/users");
      setUser(response.data);
    };
  return (
    <div class="box" style={{ width: "50%", margin: "0 auto",paddingBottom:"70px" }}>
        <div className="columns mt-5 is-centered">
      <div className="column is-half">
      <Link to={'add'} className="button is-normal is-primary is-responsive is-rounded" >Tambah</Link>  
        <table className="table is-striped is-narrow is-hoverable is-fullwidth">
          <thead>
            <tr>
              <th>No</th>
              <th>Name</th>
              <th>Email</th>
              <th>Point</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index)=>(
                <tr key={user._id}>
                <td>{index +1}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.point}</td>
                <td>
                  <button className="button is-normal is-warning is-responsive is-rounded">Edit</button>
                  <button className="button is-normal is-danger ml-2 is-responsive is-rounded">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      
    </div>
    </div>
  )
}

export default UserList
