import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";

const DetailUser = () => {
  const [user, setUser] = useState({});
  const [pelanggarans, setPelanggarans] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [point, setPoint] = useState("");
  const { id } = useParams();

  // Tambahkan state loading untuk menunjukkan bahwa data sedang diambil
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Mengambil data pengguna berdasarkan ID
    axios
      .get(`http://localhost:5000/users/${id}`)
      .then((response) => {
        setUser(response.data);
        setLoading(false); // Set loading menjadi false setelah data diterima
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
      });

    // Mengambil semua data pelanggaran
    axios
      .get("http://localhost:5000/pelanggarans")
      .then((response) => {
        // Menerapkan filter pada pelanggaran berdasarkan ID pengguna
        const filteredPelanggarans = response.data.filter(
          (pelanggaran) => pelanggaran.user === id
        );

        // Menghitung total Kurangpoint dari pelanggaran-pelanggaran tersebut
        const totalKurangpoint = filteredPelanggarans.reduce(
          (total, pelanggaran) => total + pelanggaran.Kurangpoint,
          0
        );

        // Mengupdate state pelanggarans
        setPelanggarans(filteredPelanggarans);

        // Menghitung nilai baru untuk user.point
        const newValueForPoint = 100 - totalKurangpoint;

        // Mengirim permintaan PATCH ke server untuk memperbarui user.point
        axios
          .patch(`http://localhost:5000/users/${id}`, {
            point: newValueForPoint,
          })
          .then((response) => {
            // Jika pembaruan berhasil, perbarui state user
            setUser(response.data);
          })
          .catch((error) => {
            console.error("Error updating user point:", error);
          });
      })
      axios 
      .catch((error) => {
        console.error("Error fetching pelanggaran data:", error);
      });
  }, [id]);
  useEffect(() => {
    getUserById();
  },[]);

  const getUserById = async()=>{
    const response = await axios.get(`http://localhost:5000/users/${id}`);
    setName(response.data.name);
    setEmail(response.data.email);
    setPoint(response.data.point);
  }

  // Untuk mengubah format tanggal
  function formatDate(dateString) {
    const options = { year: "numeric", month: "2-digit", day: "2-digit" };
    const dateObj = new Date(dateString);
    return dateObj.toLocaleDateString("en-US", options);
  }

  return (
    <div
      className="box"
      style={{
        width: "75%",
        margin: "0 auto",
        marginTop: "50px",
        paddingBottom: "70px",
      }}
    >
      <div>
        <h1
          style={{
            textAlign: "center",
            fontSize: "2rem",
            marginBottom: "30px",
          }}
        >
          Detail Informasi Pelanggaran
        </h1>
      </div>
      {loading ? ( // Tampilkan pesan loading jika data sedang diambil
        <p>Loading...</p>
      ) : (
        <div>
          <div className="columns">
            <div className="column is-1">Nama : </div>
            <div className="column">{name}</div>
          </div>
          <div className="columns">
            <div className="column is-1">Email : </div>
            <div className="column">{email}</div>
          </div>
          <div className="columns">
            <div className="column is-1">Point : </div>
            <div className="column">{point}</div>
          </div>
          <div className="container mt-5">
            <div className="columns is-centered">
              <div className="column is-four-fifths">
                <Link
                  to={"add/pelanggaran/"} // Anda mungkin perlu mengganti ini sesuai dengan rute yang benar
                  className="button is-primary is-rounded"
                >
                  Tambah
                </Link>
                <div className="table-container">
                  <table className="table is-fullwidth is-striped is-hoverable is-responsive">
                    <thead>
                      <tr>
                        <th>No</th>
                        <th>Tanggal</th>
                        <th>Pelanggaran</th>
                        <th>Pengurangan Point</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {pelanggarans.map((pelanggaran, index) => (
                        <tr key={pelanggaran._id}>
                          <td>{index + 1}</td>
                          <td>{formatDate(pelanggaran.date)}</td>
                          <td>{pelanggaran.keterangan}</td>
                          <td>{pelanggaran.Kurangpoint}</td>
                          <td>
                            <Link
                              to={`edit/${pelanggaran._id}`}
                              className="button is-warning is-rounded"
                            >
                              Edit
                            </Link>
                            <button className="button is-danger is-rounded">
                              Delete
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DetailUser;
