import React, { useState,useEffect} from "react";
import { Link} from "react-router-dom";
import axios from "axios";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { useNavigate } from "react-router-dom";
import { savePelanggaran } from "../../../Backend/controller/PelanggaranController";

const AddPelanggaran = () => {
  const [date, setDate] = useState("");
  const [keterangan, setKeterangan] = useState("");
  const [Kurangpoint, setKurangpoint] = useState(0);
  const navigate = useNavigate();

  const savePelanggaran = async(e)=>{
    e.preventDefault();
    try {
        await axios.post('http://localhost:5000/pelanggarans',{
            
        })
    } catch (error) {
        
    }
  }

  const [selectedDate, setSelectedDate] = useState(null);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };
  return (
    <div className="">
      <div
        class="box"
        style={{
          width: "50%",
          margin: "0 auto",
          marginTop: "50px",
          paddingBottom: "70px",
        }}
      >
        <div className="columns mt-5 is-centered">
          <div className="column is-half">
            <form onSubmit={savePelanggaran}>
              <div className="field">
              <label className="label">Input Tanggal</label>
                <div className="control">
                <DatePicker
                    selected={selectedDate}
                    className="input"
                    value={date}
                    onChange={(handleDateChange,e)=> setDate(e.target.value)} 
                    dateFormat="dd/MM/yyyy" // Format tampilan tanggal sesuai keinginan Anda
                    isClearable // Membuat opsi untuk menghapus tanggal
                  />
                </div>
              </div>
              <div className="field">
                <label className="label">Email</label>
                <div className="control">
                  <input type="text" className="input" value={keterangan} onChange={(e)=> setKeterangan(e.target.value)} placeholder="Email" />
                </div>
              </div>
              <div className="field">
                <label className="label">Point</label>
                <div className="control">
                  <input type="number" className="input" value={Kurangpoint} onChange={(e)=> setKurangpoint(e.target.value)} placeholder="Point" />
                </div>
              </div>
              <div className="field">
                <button className="button is-success" type="submit">
                  Tambah
                </button>
                <Link to={"/"} className="button is-normal is-danger ml-2">
                  Menu Utama
                </Link>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddPelanggaran;
