import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";

const DetailUser = () => {
  const [user, setUser] = useState({});
  const [pelanggarans, setPelanggarans] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    // Ambil data pengguna berdasarkan ID
    axios.get(`http://localhost:5000/users/${id}`)
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
      });

    // Ambil semua data pelanggaran
    axios.get("http://localhost:5000/pelanggarans")
      .then((response) => {
        // Filter pelanggaran berdasarkan ID pengguna
        const filteredPelanggarans = response.data.filter((pelanggaran) => pelanggaran.user === id);
        setPelanggarans(filteredPelanggarans);
      })
      .catch((error) => {
        console.error("Error fetching pelanggaran data:", error);
      });
  }, [id]);

  const [penguranganPoint, setPenguranganPoint] = useState(0);

  // Fungsi untuk melakukan pengurangan point pada pelanggaran tertentu
  const handlePenguranganPoint = (pelanggaranId) => {
    if (penguranganPoint > 0) {
      // Mengirim permintaan ke server untuk mengurangkan point pada pelanggaran
      axios.post(`http://localhost:5000/pelanggarans/${pelanggaranId}/subtract-point`, {
        amount: penguranganPoint,
      })
        .then((response) => {
          // Perbarui data pelanggaran setelah pengurangan point berhasil
          const updatedPelanggarans = pelanggarans.map((pelanggaran) => {
            if (pelanggaran._id === pelanggaranId) {
              return response.data;
            }
            return pelanggaran;
          });
          setPelanggarans(updatedPelanggarans);

          // Reset nilai pengurangan point
          setPenguranganPoint(0);
        })
        .catch((error) => {
          console.error("Error subtracting point:", error);
        });
    }
  };
  return (
    <div className="box" style={{ width: "75%", margin: "0 auto", marginTop: "50px", paddingBottom: "70px" }}>
      <div><h1 style={{ textAlign: "center", fontSize: "2rem", marginBottom: "30px" }}>Detail Informasi Pelanggaran</h1></div>
      <div className="columns">
        <div className="column is-1">Nama : </div>
        <div className="column">{user.name}</div>
      </div>
      <div className="columns">
        <div className="column is-1">Email : </div>
        <div className="column">{user.email}</div>
      </div>
      <div className="columns">
        <div className="column is-1">Point : </div>
        <div className="column">{user.point}</div>
      </div>
      <div className="container mt-5">
        <div className="columns is-centered">
          <div className="column is-four-fifths">
            <Link to={"add/pelanggaran"} className="button is-primary is-rounded">
              Tambah
            </Link>
            <div className="table-container">
              <table className="table is-fullwidth is-striped is-hoverable is-responsive">
                <thead>
                  <tr>
                    <th>No</th>
                    <th>Tanggal</th>
                    <th>Pelanggaran</th>
                    <th>Pengurangan Point</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
      {pelanggarans.map((pelanggaran, index) => (
        <tr key={pelanggaran._id}>
          <td>{index + 1}</td>
          <td>{pelanggaran.date}</td>
          <td>{pelanggaran.keterangan}</td>
          <td>{pelanggaran.Kurangpoint}</td>
          <td>
            {/* Tombol "Kurangkan Point" dan input pengurangan point */}
            <div className="field">
              <div className="control">
                <input
                  type="number"
                  className="input is-small"
                  value={penguranganPoint}
                  onChange={(e) => setPenguranganPoint(parseInt(e.target.value))}
                />
                <button
                  className="button is-small is-success"
                  onClick={() => handlePenguranganPoint(pelanggaran._id)}
                >
                  Kurangkan Point
                </button>
              </div>
            </div>
            {/* Tombol "Edit" dan "Delete" */}
            <Link to={`edit/${pelanggaran._id}`} className="button is-warning is-rounded is-small">
              Edit
            </Link>
            <button className="button is-danger is-rounded is-small">
              Delete
            </button>
          </td>
        </tr>
      ))}
    </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetailUser;
