import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const UserList = () => {
  const [users, setUser] = useState([]);
  const [pelanggarans, setPelanggarans] = useState([]);

  useEffect(() => {
    // Mengambil data pengguna berdasarkan ID
    axios
      .get(`http://localhost:5000/users/${id}`)
      .then((response) => {
        setUser(response.data);
        setLoading(false); // Set loading menjadi false setelah data diterima
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
      });

    // Mengambil semua data pelanggaran
    axios
      .get("http://localhost:5000/pelanggarans")
      .then((response) => {
        // Menerapkan filter pada pelanggaran berdasarkan ID pengguna
        const filteredPelanggarans = response.data.filter(
          (pelanggaran) => pelanggaran.user === id
        );

        // Menghitung total Kurangpoint dari pelanggaran-pelanggaran tersebut
        const totalKurangpoint = filteredPelanggarans.reduce(
          (total, pelanggaran) => total + pelanggaran.Kurangpoint,
          0
        );

        // Mengupdate state pelanggarans
        setPelanggarans(filteredPelanggarans);

        // Menghitung nilai baru untuk user.point
        const newValueForPoint = 100 - totalKurangpoint;

        // Mengirim permintaan PATCH ke server untuk memperbarui user.point
        axios
          .patch(`http://localhost:5000/users/${id}`, {
            point: newValueForPoint,
          })
          .then((response) => {
            // Jika pembaruan berhasil, perbarui state user
            setUser(response.data);
          })
          .catch((error) => {
            console.error("Error updating user point:", error);
          });
      })
      .catch((error) => {
        console.error("Error fetching pelanggaran data:", error);
      });
  }, [id]);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    const response = await axios.get("http://localhost:5000/users");
    setUser(response.data);
  };
  
  return (
    <div className="container mt-5">
      <div className="columns is-centered">
        <div className="column is-four-fifths">
          <Link to={"add"} className="button is-primary is-rounded">
            Tambah
          </Link>
          <div className="table-container">
            <table className="table is-fullwidth is-striped is-hoverable is-responsive">
              <thead>
                <tr>
                  <th>No</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Point</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user, index) => (
                  <tr key={user._id}>
                    <td>{index + 1}</td>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.point}</td>
                    <td>
                      <Link to={`edit/${user._id}`} className="button is-warning is-rounded">
                        Edit
                      </Link>
                      <button className="button is-danger is-rounded">
                        Delete
                      </button>
                      <Link to={`detail/${user._id}`} className="button is-info is-rounded">
                        Detail
                      </Link>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserList;
