import React, { useState,use} from "react";
import { Link} from "react-router-dom";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const AddPelanggaran = () => {
    const [users, setUser] = useState([]);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    const response = await axios.get("http://localhost:5000/users");
    setUser(response.data);
  };
  const [selectedDate, setSelectedDate] = useState(null);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };
  return (
    <div className="">
      <div
        class="box"
        style={{
          width: "50%",
          margin: "0 auto",
          marginTop: "50px",
          paddingBottom: "70px",
        }}
      >
        <div className="columns mt-5 is-centered">
          <div className="column is-half">
            <form>
              <div className="field">
              <label className="label">Input Tanggal</label>
                <div className="control">
                <DatePicker
                    selected={selectedDate}
                    className="input"
                    onChange={handleDateChange} 
                    dateFormat="dd/MM/yyyy" // Format tampilan tanggal sesuai keinginan Anda
                    isClearable // Membuat opsi untuk menghapus tanggal
                  />
                </div>
              </div>
              <div className="field">
                <label className="label">Email</label>
                <div className="control">
                  <input type="text" className="input" placeholder="Email" />
                </div>
              </div>
              <div className="field">
                <label className="label">Point</label>
                <div className="control">
                  <input type="number" className="input" placeholder="Point" />
                </div>
              </div>
              <div className="field">
                <button className="button is-success" type="submit">
                  Tambah
                </button>
                <Link to={`detail/${user._id}`} className="button is-normal is-danger ml-2">
                  Kembali
                </Link>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddPelanggaran;
