import React, { useState, useEffect } from "react";
import axios from "axios";
import {useParams } from "react-router-dom";

const DetailUser = () => {
  const [users, setUser] = useState([]);
  const {id} = useParams();

  useEffect(() => {
    getUserById();
  }, []);

  const getUserById = async () => {
    const response = await axios.get(`http://localhost:5000/users/${id}`);
    setUser(response.data);
  };
  return (
    <div
      className="box"
      style={{
        width: "50%",
        margin: "0 auto",
        marginTop: "50px",
        paddingBottom: "70px",
      }}
    >
        {users.map((user, index) => (
                  <tr key={user._id}>
                    <td>{index + 1}</td>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.point}</td>
                    <td>
                      <Link to={`edit/${user._id}`} className="button is-warning is-rounded">
                        Edit
                      </Link>
                      <button className="button is-danger is-rounded">
                        Delete
                      </button>
                      <Link to={`detail/${user._id}`} className="button is-info is-rounded">
                        Detail
                      </Link>
                    </td>
                  </tr>
                ))}
      <div class="columns">
        <div class="column is-1">Nama : </div>
        <div class="column">Second column</div>
      </div>
      <div class="columns">
        <div class="column is-1">Email : </div>
        <div class="column">Second column</div>
      </div>
      <div class="columns">
        <div class="column is-1">Point : </div>
        <div class="column">Second column</div>
      </div>
    </div>
  );
};

export default DetailUser;
